import { Typography, Link } from "@mui/material";
import { Box } from "@mui/system";
import React, { useEffect, useState } from "react";
import { Link as RouterLink } from "react-router-dom"; // Import Link from react-router-dom
import { getAllMovies } from "../../helpers/api-helpers";
import CardLayout from "../HomePage/CardLayout";

const AllMovies = () => {
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    getAllMovies()
      .then((data) => setMovies(data))
      .catch((err) => console.log(err));
  }, []);

  return (
    <Box margin="auto" marginTop={4} maxWidth="1200px">
      <Typography variant="h4" textAlign="center" mb={4}>
        All Movies
      </Typography>
      <Box display="flex" justifyContent="center" flexWrap="wrap" gap={4}>
        {movies && movies.map((movie, index) => (
          <CardLayout
            key={index}
            id={movie._id}
            title={movie.title}
            releaseDate={movie.releaseDate}
            posterUrl={movie.posterUrl}
            description={movie.description}
          />
        ))}
      </Box>
      <Box display="flex" justifyContent="center" marginTop={3}>
        <Link component={RouterLink} to="/booking" underline="none">
          <Typography variant="body1" color="primary">
            View More
          </Typography>
        </Link>
      </Box>
    </Box>
  );
};

export default AllMovies;
