import { Box, Button, Typography } from "@mui/material";
import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { getAllMovies } from "../api-helpers/api-helpers";
import MovieItem from "./Movies/MovieItem";

const HomePage = () => {
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    getAllMovies()
      .then((data) => setMovies(data.movies))
      .catch((err) => console.log(err));
  }, []);

  return (
    <Box width="100%" margin="auto" marginTop={2}>
      <Box margin="auto" width="80%" height="40vh" padding={2} borderRadius={8} overflow="hidden" boxShadow={3}>
        <img
          src="https://i.ytimg.com/vi/bweRG6WueuM/maxresdefault.jpg"
          alt="Brahmastra"
          width="100%"
          height="100%"
          style={{ objectFit: "cover" }}
        />
      </Box>
      <Box padding={5} margin="auto">
        <Typography variant="h4" textAlign="center" color="#2b2d42">
          Latest Releases
        </Typography>
      </Box>
      <Box
        margin="auto"
        display="flex"
        justifyContent="center"
        alignItems="center"
        flexWrap="wrap"
      >
        {movies.slice(0, 4).map((movie) => (
          <MovieItem
            key={movie._id}
            id={movie._id}
            title={movie.title}
            posterUrl={movie.posterUrl}
            releaseDate={movie.releaseDate}
          />
        ))}
      </Box>
      <Box display="flex" padding={5} margin="auto">
        <Button
          component={Link}
          to="/movies"
          variant="contained"
          sx={{ margin: "auto", backgroundColor: "#2b2d42", color: "#fff", "&:hover": { backgroundColor: "#4a4e67" } }}
        >
          View All Movies
        </Button>
      </Box>
    </Box>
  );
};

export default HomePage;
