import { Box } from "@mui/system";
import React, { Fragment, useEffect, useState } from "react";
import { getAdminById } from "../api-helpers/api-helpers";
import AccountCircleIcon from "@mui/icons-material/AccountCircle";
import { List, ListItem, ListItemText, Typography } from "@mui/material";
import axios from "axios";
const AdminProfile = () => {
  const [admin, setAdmin] = useState();
  const [fetchecMovie,setFetchedMovie]= useState([]);
  useEffect(() => {
    getAdminById()
      .then((res) => {setAdmin(res.admin);setFetchedMovie(res.admin.addedMovies)})
      .catch((err) => console.log(err));
  }, []);

  const deleteMovieById = async (id) => {
await axios.delete(`http://localhost:5000/movie/${id}`).then((data)=>{
  console.log(data);
}).catch((err)=>console.log(err))
  }

  return (
    <Box width={"100%"} display="flex">
      <Fragment>
        {" "}
        {admin && (
          <Box
            flexDirection={"column"}
            justifyContent="center"
            alignItems={"center"}
            width={"30%"}
            padding={3}
          >
            <AccountCircleIcon
              sx={{ fontSize: "10rem", textAlign: "center", ml: 3 }}
            />

            <Typography
              mt={1}
              padding={1}
              width={"auto"}
              textAlign={"center"}
              border={"1px solid #ccc"}
              borderRadius={6}
            >
              Email: {admin.email}
            </Typography>
          </Box>
        )}
        {admin && admin.addedMovies.length > 0 && (
          <Box width={"70%"} display="flex" flexDirection={"column"}>
            <Typography
              variant="h3"
              fontFamily={"verdana"}
              textAlign="center"
              padding={2}
            >
              Added Movies
            </Typography>
            <Box
              margin={"auto"}
              display="flex"
              flexDirection={"column"}
              width="80%"
            >
              <List>
                {fetchecMovie.map((movie, index) => (
                 <>
                  <ListItem
                    sx={{
                      bgcolor: "#00d386",
                      color: "white",
                      textAlign: "center",
                      margin: 1,
                    }}
                  >
                    <ListItemText
                      sx={{ margin: 1, width: "auto", textAlign: "left" }}
                    >
                      Movie: {movie.title}

                    </ListItemText>
                  <button className="btn btn-primary" onClick={()=>{deleteMovieById(movie._id)}}>Delete Me</button>
                  </ListItem>
                  </>
                ))}
              </List>
            </Box>
          </Box>
        )}
      </Fragment>
    </Box>
  );
};

export default AdminProfile;
