import React, { useState } from "react";
import {
  Box,
  Button,
  Checkbox,
  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,
  DialogTitle,
  FormLabel,
  TextField,
  Typography,
} from "@mui/material";
import { addMovie } from "../../api-helpers/api-helpers";

const AddMovie = () => {
  const [inputs, setInputs] = useState({
    title: "",
    description: "",
    posterUrl: "",
    releaseDate: "",
    featured: false,
  });
  const [actors, setActors] = useState([]);
  const [actor, setActor] = useState("");
  const [open, setOpen] = useState(false);

  const handleChange = (e) => {
    setInputs((prevState) => ({
      ...prevState,
      [e.target.name]: e.target.value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    addMovie({ ...inputs, actors })
      .then((res) => {
        console.log(res);
        setOpen(true); // Open the modal on successful submission
      })
      .catch((err) => console.log(err));
  };

  const handleClose = () => {
    setOpen(false); // Close the modal
    // Reset form fields and actors list
    setInputs({
      title: "",
      description: "",
      posterUrl: "",
      releaseDate: "",
      featured: false,
    });
    setActors([]);
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        {/* Form content */}
      </form>
      {/* Popup notification */}
      <Dialog open={open} onClose={handleClose}>
        <DialogTitle>Movie Added Successfully!</DialogTitle>
        <DialogContent>
          <DialogContentText>
            The movie has been added to the platform.
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="primary">
            Close
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
};

export default AddMovie;
